function iziToastShow(o,r,i=1){let e=switchSettings(i);iziToast.show({title:o,titleColor:"white",fontsize:14,message:r,messageSize:14,messageColor:"white",position:"topRight",maxWidth:500,backgroundColor:e.backgroundColor,progressBarColor:e.progressBarColor,iconUrl:e.iconUrl,layout:2,displayMode:2})}function iziToastHide(){let o=document.querySelector(".iziToast")||null;o&&iziToast.hide({},o)}function switchSettings(o){switch(o){case 1:default:return{backgroundColor:"#499ED7",progressBarColor:"#2E8FD1",iconUrl:"/assets/material.io/baseline-info-24px-white.svg"};case 2:return{backgroundColor:"#FF9800",progressBarColor:"#CF6E00",iconUrl:"/assets/material.io/baseline-warning-24px-white.svg"};case 3:return{backgroundColor:"#EC2027",progressBarColor:"#C81117",iconUrl:"/assets/material.io/baseline-error-24px-white.svg"}}}